import axios from "axios";
import { useEffect, useState } from "react";
import AuthButton from "../components/AuthButton";
import { jwtDecode } from "jwt-decode";

interface toDo {
  id: number
  title: string
}

const getUserId = () => {
  const token = localStorage.getItem('Bearer');
  if (typeof token === 'string') {
    const decodedToken = jwtDecode(token);
  }
  return 
}

const Posts = () => {
  const [posts, setPosts] = useState<toDo[]>([]);
  const [title, setTitle] = useState('');

  const fetchPosts = async () => {
    const response = await axios.get('http://localhost:5000/api/post/posts');
    setPosts(response.data);
  }

  useEffect(() => {
    fetchPosts();
  }, []);

  const addPost = async (e: any) => {
    e.preventDefault();
    const newPost = {title};
    await axios.post('http://localhost:5000/api/post/posts', newPost);
    setTitle('');
    fetchPosts();
  }

  const delPost = async (id: any) => {
    await axios.delete(`http://localhost:5000/api/post/posts/${id}`);
    fetchPosts();
  }

  const updPost = async (id: any, data: string) => {
    const newData = window.prompt(data, data);
    if (newData !== null) {
      await axios.put(`http://localhost:5000/api/post/posts/${id}`, {title: newData});
      fetchPosts();
    }
  }

  return (
    <div className='Posts'>
      <AuthButton />
      <form onSubmit={addPost}>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <button type="submit">Добавить задачу</button>
        </form>
        <h1>Список задач</h1>
        {posts.map((post) => (
          <div>
            <p>
              {post.title}
              <button onClick={() => updPost(post.id, post.title)}>Изменить</button>
              <button onClick={() => delPost(post.id)}>Удалить</button>
            </p>
          </div>
        ))}
        <div>
          <button onClick={() => alert(localStorage.getItem('Bearer'))}>Токен</button>
        </div>
    </div>
  )
}

export default Posts
