import { Link, useNavigate } from "react-router-dom"
import { HOME_ROUTE, REGISTRATION_ROUTE } from "../utils/consts"
import '../styles.css'
import React, { useState } from "react";
import axios from "axios";

const Auth = () => {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate(HOME_ROUTE)
  };

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (event: SubmitEvent) => {
    event.preventDefault();
    setError('');

    try {
      const userData = {username, password}
      const response = await axios.post('http://localhost:5000/api/user/login', userData);
      if (response.data && response.data.to)
      localStorage.setItem('Bearer', response.data.token);
    } catch (error) {
      
    }
  }

  return (
    <div className='Login'>
      <h4>Войти</h4>
      <form>
        <div>
          <label htmlFor="login">Логин</label>
          <input type="text" />
        </div>
        <div>
          <label htmlFor="password">Пароль</label>
          <input type="text" />
        </div>
        <button onClick={handleClick}>Войти</button>
        <Link to={REGISTRATION_ROUTE}>Регистрация</Link>
      </form>
    </div>
  )
}

export default Auth
