import axios from "axios";
import { useEffect, useState } from "react";

interface toDo {
  id: number
  title: string
}

const App = () => {

  const [posts, setPosts] = useState<toDo[]>([]);
  const [title, setTitle] = useState('');
  const [id, setId] = useState(0);

  const fetchPosts = async () => {
    const response = await axios.get('http://localhost:5000/posts');
    setPosts(response.data);
  }

  useEffect(() => {
    fetchPosts();
  }, []);

  const addPost = async (e: any) => {
    e.preventDefault();
    const newPost = {id};
    await axios.post('http://localhost:5000/posts', newPost);
    setTitle('');
    fetchPosts();
  }

  const delPost = async (e: any) => {
    e.preventDefault();
    const newPost = {title};
    await axios.delete('http://localhost:5000/posts/:postId', newPost);
    setTitle('');
    fetchPosts();
  }

  return (
    <div className='App'>
      <header className='App-header'>
        <h1>Главная</h1>
        <form onSubmit={addPost}>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <button type="submit">Добавить задачу</button>
        </form>
        <h1>Список задач</h1>
        {/* <button>Все задачи</button><button>Активные задачи</button><button>Завершенные задачи</button> */}
        {posts.map((post) => (<div><p>{post.title}<button>Удалить</button></p></div>))}
      </header>
    </div>
  )
}

export default App;
